# -*- coding: utf-8 -*-
from enum import Enum


# constant values of fonts's styles
class FontStyle(Enum):

    DEFAULT = '0'

    HIGHLIGHT = '1'

    NO_BOLD = '22'

    UNDERLINE = '4'

    NO_UNDERLINE = '24'

    SPARKLE = '5'

    NO_SPARKLE = '25'

    REVERSE_DISPLAY = '7'

    NO_REVERSE_DISPLAY = '27'
