import random

class UserAgent():
    def __init__(self):
        self.win_user_agents = win_user_gent
        self.android_user_gent = android_user_gent

    def random_header(self, system="windows"):
        if system == "windows":
            user_agent = random.choice(self.win_user_agents)
        elif system == "android":
            user_agent = random.choice(self.android_user_gent)
        else:
            user_agent = random.choice(self.win_user_agents)
        return user_agent

