	b->uu_buf_pos = b->uu_ch_buf = base;
	b->uu_is_our_buffer = 0;
	b->uu_input_file = 0;
	b->uu_n_chars = b->uu_buf_size;
	b->uu_is_interactive = 0;
	b->uu_at_bol = 1;
	b->uu_fill_buffer = 0;
	b->uu_buffer_status = YY_BUFFER_NEW;

