/* uulex_destroy is for both reentrant and non-reentrant scanners. */
int uulex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		uu_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		uupop_buffer_state();
	}

	/* Destroy the stack itself. */
	uufree((uu_buffer_stack) );
	(uu_buffer_stack) = NULL;

    uufree ( (uu_state_buf) );
    (uu_state_buf)  = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * uulex() is called, initialization will occur. */
    uu_init_globals( );

    return 0;
}

